import React from 'react';
import { ModalAddCart } from '../ModalAddCart/ModalAddCart';
import { Product } from '../Product/Product';
import { toggleFavorite, isFavorite } from "../utils/favorite";
import { EMPTY_HEART, FULL_HEART } from '../utils/ico';
import '../ProductList/ProductList.scss';

export const FavoriteList = ({ products, setModalProductId, modalProductId, handleModalClose, handleModalConfirm }) => {

    const handleFavorite = (productId) => {
        toggleFavorite(productId);
        // isFavorite(productId) ? setColorSvg(FULL_HEART) : setColorSvg(EMPTY_HEART);
        handleFavorite();
    }

    return (
        <div className="ProductList ">

            {products.length > 0 &&
                products.map(product =>
                    <Product
                        key={product.id}
                        product={product}
                        onAddToCart={() => setModalProductId(product.id)}
                        colorSvg={isFavorite(product.id) ? FULL_HEART : EMPTY_HEART}
                        handleFavorite={() => handleFavorite(product.id)}
                    />
                )
            }

            {products.length === 0 &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Favorite products not selected
                    </p>
                </div>
            }

            {modalProductId &&
                <ModalAddCart
                    onConfirm={handleModalConfirm}
                    onClose={handleModalClose}
                />
            }
        </div>
    )
}
