import React from 'react';
import { ModalAddCart } from '../ModalAddCart/ModalAddCart';
import { Product } from '../Product/Product';
import { getProductFavorite } from '../utils/favorite';
import '../ProductList/ProductList.scss';



export const FavoriteList = ({ products, setModalProductId, modalProductId, handleModalClose, handleModalConfirm, render }) => {

    const favoriteListItems = getProductFavorite();
    const favoriteList = products.filter(product => favoriteListItems.find(item => item === product.id))

    console.log(favoriteListItems);
    return (
        <div className="ProductList ">

            {favoriteListItems.length > 0 &&
                favoriteList.map(product =>
                    <Product
                        key={product.id}
                        product={product}
                        onAddToCart={() => setModalProductId(product.id)}
                        render={render}
                    />
                )
            }

            {favoriteListItems.length === 0 &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Favorite products not selected
                    </p>
                </div>
            }

            {modalProductId &&
                <ModalAddCart
                    onConfirm={handleModalConfirm}
                    onClose={handleModalClose}
                />
            }
        </div>
    )
}
