class Favorite {

    addFavoriteItem(item) {

        const favorite = JSON.parse(localStorage.getItem('favoriteList'));
        console.log(favorite);
        favorite = item;

        localStorage.setItem('favoriteList', true);
        localStorage.removeItem();

    }


}

export default Favorite;
