import React from 'react';
import { Product } from '../Product/Product';
import { getProductWithCart } from '../utils/cart';


export const CartList = ({ products }) => {

    const cartList = getProductWithCart();

    return (
        <div>
            {getProductWithCart &&
                products.map((product) =>
                    cartList.map((itemID) => <Product
                        key={itemID}
                        product={product}

                    />)
                    && <button>X</button>
                )
            }
            {getProductWithCart &&
                <div className="row align-items-center justify-content-center">
                    <p className="notification">Your Shopping Cart is Empty</p>
                </div>
            }
        </div>
    )
}
