import React, { Component } from "react";
import Card from "../Card/Card";
import ModalAddCart from "../ModalAddCart/ModalAddCart";
import './ContainerRoot.scss'
import addToCart from "../utilits/cart.js";




class ContainerRoot extends Component {

  constructor(props) {
    super(props)

    this.state = {
      products: [],
      modalProductId: null
    }
  }

  async componentDidMount() {
    const response = await fetch('/cardProduct.json');
    const result = await response.json();
    this.setState({ products: result });
    console.log(result);
  }

  handleModalClose = () => this.setState({ modalProductId: null })

  handleModalConfirm = () => {
    addToCart(this.state.modalProductId);
    this.handleModalClose();
  }


  render() {
    console.log(this.state.products);

    return (
      <div className="ContainerRootStyled">
        {this.state.products.map(product =>
          <Card
            key={product.id}
            product={product}
            onAdd={() => this.setState({ modalProductId: product.id })}
          />
        )}

        {this.state.modalProductId &&
          <ModalAddCart
            onConfirm={this.handleModalConfirm}
            onClose={this.handleModalClose}
          />
        }
      </div>
    );
  }
}

export default ContainerRoot;
