import React, { Component } from 'react';
import './Product.scss';
import Button from '../Button/Button';
import FavoriteButton from '../FavoriteButton/FavoriteButton';
import addToFavorite from "../utilities/favorite";
import { EMPTY_HEART, FULL_HEART } from '../utilities/ico';

class Product extends Component {

  constructor(props) {
    super(props)

    this.state = {
      colorSVG: EMPTY_HEART
    }
  }

  handleFavorite = (productId) => {
    addToFavorite(productId) ? this.setState({ colorSVG: FULL_HEART }) : this.setState({ colorSVG: EMPTY_HEART })
  }

  render() {
    const { name, url, price, id, color, } = this.props.product;
    const { onAddToCart } = this.props;
    return (
      <div
        className="Product"
      >
        <div
          className="row justify-cont-between"
        >
          <img
            className="Product__Img"
            src={url}
            alt={name}
          />

          <FavoriteButton
            onClick={this.handleFavorite(id)}
            width="20"
            height="17"
            fill="#FF6767"
            viewBox="0 0 20 17"
            xmlns="http://www.w3.org/2000/svg"
            d={this.state.colorSVG}
          />

        </div>
        <p
          className="Product__Name">
          {name}
        </p>
        <p
          className="Product__id">
          Код: {id}
        </p>

        <p>
          <span className="Product__Color">
            Цвет:
          </span>
          {color}
        </p>

        <div className="row justify-cont-between align-items-center mt-15">
          <span className="Product__Price">
            {price}
            <span className="Product__Сurrency">
              грн
            </span>
          </span>

          <Button
            onClick={onAddToCart}
            className="btn"
            text="Add to cart"
            style={{ backgroundColor: "#0061c2" }}
          />
        </div>
      </div>
    )
  }
}

export default Product;
