import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { ModalAddCart } from '../ModalAddCart/ModalAddCart';
import { Product } from '../Product/Product';
import '../ProductList/ProductList.scss';
import { connect } from "react-redux";
import { getFavoriteList } from '../../store';



const mapStateToProps = (state) => ({
    favorites: state.favoriteProducts,
    modalProductId: state.modalProductId
})

export const FavoriteList = connect(mapStateToProps, { getFavoriteList, closeModalAdd })(({ favorites, modalProductId, getFavoriteList, closeModalAdd }) => {

    useEffect(() => getFavoriteList(), [])

    return (
        <div className="ProductList">

            {favorites.length > 0 ?

                favorites.map(product =>
                    <Product
                        key={product.id}
                        product={product}
                    />
                )

                : <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Favorite products not selected
                    </p>
                </div>
            }

            {modalProductId &&
                <ModalAddCart
                    onConfirm={handleModalConfirm}
                    onClose={() => closeModalAdd(product.id)}
                />
            }
        </div>
    );
})

FavoriteList.propTypes = {
    favorites: PropTypes.object,
    modalProductId: PropTypes.object,
    getFavoriteList: PropTypes.func,
    handleModalClose: PropTypes.func,
    handleModalConfirm: PropTypes.func
}
