import React, { Component } from "react";
import Product from "../Product/Product";
import ModalAddCart from "../ModalAddCart/ModalAddCart";
import './ProductList.scss'
import addToCart from "../utilities/cart.js";
import addToFavorite from "../utilities/favorite";
import { EMPTY_HEART, FULL_HEART } from '../utilities/ico';

class ProductList extends Component {


  constructor(props) {
    super(props)

    this.state = {
      products: [],
      modalProductId: null,
    }
  }

  async componentDidMount() {
    const response = await fetch('/cardProduct.json');
    const result = await response.json();
    this.setState({ products: result });
  }

  handleModalClose = () => this.setState({ modalProductId: null })

  handleModalConfirm = () => {
    addToCart(this.state.modalProductId);
    this.handleModalClose();
  }

  handleFavorite = (productId) => {
    addToFavorite(productId) ? this.setState({ colorSVG: FULL_HEART }) : this.setState({ colorSVG: EMPTY_HEART });

  }

  render() {
    return (
      <div className="ProductList">
        {this.state.products.map((product) => {
          return <Product
            key={product.id}
            product={product}
            onAddToCart={() => this.setState({ modalProductId: product.id })}
            onAddToFavorite={() => this.handleFavorite(product.id)}
          />;
        }
        )}

        {this.state.modalProductId &&
          <ModalAddCart
            onConfirm={this.handleModalConfirm}
            onClose={this.handleModalClose}
          />
        }
      </div>
    );
  }
}

export default ProductList;
