import React, { useState, useEffect } from "react";
import { Product } from "../Product/Product";
import { ModalAddCart } from "../ModalAddCart/ModalAddCart";
import './ProductList.scss'
import { addToCart } from "../utils/cart.js";


export const ProductList = () => {

  const [modalProductId, setModalProductId] = useState(null);
  const [products, setProducts] = useState([])

  const getProductList = async () => {
    const response = await fetch('cardProduct.json');
    const result = await response.json();
    setProducts(prev => [...prev, ...result]);
    console.log(result);
  }

  const handleModalClose = () => setModalProductId(modalProductId);

  const handleModalConfirm = () => {
    addToCart(modalProductId);
    handleModalClose();
  }

  useEffect(() => getProductList(), []);


  return (
    <div className="ProductList">
      {products.map((product) => {
        return <Product
          key={product.id}
          product={product}
          onAddToCart={() => setModalProductId(product.id)}
        />;
      }
      )}

      {modalProductId &&
        <ModalAddCart
          onConfirm={handleModalConfirm}
          onClose={handleModalClose}
        />
      }
    </div>
  );
}
