class Card extends Component {


    render() {
        const { name, url, price, vendor, color } = this.props.valueCard;
        return (
            <div className="CardWrapper">

                <img src={url} />
                <p>{name}</p>
                <p>{vendor}</p>
                <p>{color}</p>
                <div>
                    <span>{price}</span>

                    {fields.map(({ ...item }, index) => {
                        return (
                            <Modal key={index} fields={item} />
                        )
                    })}
                </div>
            </div>
        )
    }
}

export default Card;
