import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { ModalAddCart } from '../ModalAddCart/ModalAddCart';
import { Product } from '../Product/Product';
import '../ProductList/ProductList.scss';
import { getProductList, getFavoriteList } from "../../store";
import { connect } from "react-redux";


const mapStateToProps = (state) => ({
    products: state.favoriteProducts
})


export const FavoriteList = connect(mapStateToProps, { getFavoriteList, getProductList })(({ products, getFavoriteList, modalProductId, handleModalClose, handleModalConfirm, getProductList }) => {


    useEffect(() => getProductList(), [])
    useEffect(() => getFavoriteList(), [])

    return (
        <div className="ProductList">

            {products.length > 0 &&

                products.map(product =>
                    <Product
                        key={product.id}
                        product={product}
                    // favoriteListCallback={handleFavoriteList}
                    />
                )
            }

            {products.length === 0 &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Favorite products not selected
                    </p>
                </div>
            }

            {modalProductId &&
                <ModalAddCart
                    onConfirm={handleModalConfirm}
                    onClose={handleModalClose}
                />
            }
        </div>
    );
})

FavoriteList.propTypes = {
    product: PropTypes.object,
    setModalProductId: PropTypes.func,
    modalProductId: PropTypes.string,
    handleModalClose: PropTypes.func,
    handleModalConfirm: PropTypes.func
}
