    }

    return (
        <div>
            <Header />
            <div className="container">
                <h1 className="heading-page">Cart</h1>

                <CartList
                    products={products}
                    ModalDeleteID={ModalDeleteID}

                />
                {ModalDeleteID &&
                    <ModalDeleteCart
                        onClose={handleModalClose}
                        onConfirm={handleModalRemove}
                    />
                }

            </div>
        </div>
    )
}
