    let cartListId = null;

    products.map(product => cartListId = getProductWithCart(product.id));


    return (
        <div className="ProductList ">
            {getProductWithCart &&
                products.map(product =>
                    <Product
                        key={itemID}
                        product={product}

                    />
                    && <span
                        className="close"
                        onClick={onRemove}
                    >&times;
                    </span>


                )
            }
            {getProductWithCart &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Your Shopping Cart is Empty
                    </p>
                </div>
            }
        </div>
    )
}
