import React from "react";
import PropTypes from 'prop-types';


export const CartItem = ({ product, handelModalConfirm, count }) => {


    return (
        <div>
            <hr />
            <div className="row justify-cont-between">
                <img
                    className="Product__Img"
                    src={product.url}
                    alt={product.name}
                />
                <div>
                    <p
                        className="Product__Name">
                        {product.name}
                    </p>
                    <p
                        className="Product__id">
                        Код: {product.id}
                    </p>

                    <p>
                        <span className="Product__Color">
                            Цвет:
                        </span>
                        {product.color}
                    </p>
                </div>
                <div>
                    <span className="Product__Price">
                        {product.price}
                        <span className="Product__Сurrency">
                            грн
                        </span>
                    </span>
                </div>
                <div>
                    Количество: {count}
                </div>
            </div>
            <span
                className="close btn-close-remove"
                onClick={handelModalConfirm}
            >&times;
            </span>
            <hr />
        </ div >
    )
}

CartItem.propTypes = {
    product: PropTypes.object,
    handelModalConfirm: PropTypes.func,
    count: PropTypes.string
}
