import './App.scss';
import { BrowserRouter as Router, Switch, Route, Link, NavLink } from 'react-router-dom'
import { ContainerMaxWidth } from './components/ContainerMaxWidth/ContainerMaxWidth';
// import { Menu } from './components/Menu/Menu';
import { Header } from './components/Header/Header';
import { ProductPage } from './components/ProductPage/ProductPage';
import { MenuItems } from './components/MenuItems/MenuItems';


function App() {
  return (
    <Router>
      <div>
        <Header />

        <div className="Menu">
          <Link to="/">SHOP</Link>
          <Link to="/favorite">FAVORITE</Link>
          <Link to="/cart">CART</Link>
        </div>
        <Route exact path="/" ><MenuItems text="Shop" /></Route>
        <Route exact path="/favorite"><MenuItems text="Favorite" /></Route>
        <Route exact path="/cart"><MenuItems text="Cart" /></Route>

        {/* <Menu /> */}
        <ContainerMaxWidth />
        <ProductPage />
      </div>
    </Router>
  );
}

export default App;
