import React from 'react';
import { Product } from '../Product/Product';
import { getProductWithCart, removeProductCart } from '../utils/cart';
// import './ProductList/ProductList.scss';



export const CartList = ({ products }) => {

    const cartListId = getProductWithCart();

    const cartList = products.forEach((product) => {
        cartListId.map(item => {
            return (
                <Product
                    key={() => (product.id === item) ? product.id : product.id}
                    product={product}

                />
                && <span
                    className="close"
                    onClick={removeProductCart(product.id)}
                >&times;
                    </span>
            )
        }
        )
    })


    return (
        <div className="ProductList ">
            {getProductWithCart &&
                cartList
            }


            {getProductWithCart &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Your Shopping Cart is Empty
                    </p>
                </div>
            }
        </div>
    )
}
