import React from "react";
import PropTypes from 'prop-types';
import { Product } from '../Product/Product';



export const CartItem = ({ product, handelModalConfirm, count }) => {


    return (
        <div>
            <hr />
            <div className="row justify-cont-between">
                <img
                    className="Product__Img"
                    src={product.url}
                    alt={product.name}
                />
                <div>
                    <p
                        className="Product__Name">
                        {product.name}
                    </p>
                    <p
                        className="Product__id">
                        Код: {product.id}
                    </p>

                    <p>
                        <span className="Product__Color">
                            Цвет:
                        </span>
                        {product.color}
                    </p>
                    <div>
                        <span className="Product__Price">
                            {product.price}
                            <span className="Product__Сurrency">
                                грн
            </span>
                        </span>
                    </div>
                    <div>
                        Количество: {count}
                    </div>
                </div>
                <span
                    className="close"
                    onClick={handelModalConfirm}
                >&times;
                            </span>



                <Product
                    key={product.id}
                    product={product}
                />
                <span
                    className="close"
                    onClick={handelModalConfirm}
                >&times;
                            </span>
            </div>


            <hr />
        </ div >
    )
}

CartItem.propTypes = {
    // className: PropTypes.string,
    // onClick: PropTypes.func,
    // style: PropTypes.object,
    // text: PropTypes.string,
}
