    flex-wrap: wrap;
    align-items: center;
    align-content: center;
    /* justify-content:center; */
    margin-bottom: 30px;
    /* flex-direction: column; */
    max-width: 1280px;
    max-height: 1150px;
    width: 100%;
    box-sizing: border-box;
    margin-left: auto;
    margin-right: auto;

`;




class ContainerRoot extends Component {

    getDataModal = async () => {
        const response = await fetch('../public/cardProduct.json');
        const result = await response.json();
        console.log(result);
    }


    render() {
        return (
            <ContainerRootStyled>
                {/* <Card /> */}
                {/* {getDataModal()} */}
                {result}
            </ContainerRootStyled>
        )
    }
}

export default ContainerRoot;
