function addToCart(item, count) {
    const cartJson = localStorage.getItem('cardList')
    const cart = (cartJson && JSON.parse()) || {};
    console.log(cart);
    cart[item] = count + 1;
    localStorage.setItem('cardList', JSON.stringify(cart));
}



export default addToCart;
