import { createStore, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { addToCart } from '../components/utils/cart';


const [list, setList] = useState(getProductFavorite());

products = products.filter(product => list.find(item => item === product.id));

const handleFavoriteList = () => setList(getProductFavorite());


// ACTIONS

const GET_PRODUCTS = "GET_PRODUCTS";
const SHOW_MODAL_ADD_TO_CARD = 'SHOW_MODAL_ADD_TO_CARD';
const MODAL_CLOSE = 'MODAL_CLOSE';
const FAVORITE_PRODUCT = 'FAVORITE_PRODUCT';
const SHOW_MODAL_DELETE = 'SHOW_MODAL_DELETE'


// REDUCER

const initialState = {
    products: [],
    modalProductId: null,
    modalDeleteID: null,
    favoriteProducts: [],
    modalDelete: null
}

function reducer(state = initialState, action) {
    switch (action.type) {
        case GET_PRODUCTS:
            return {
                ...state,
                products: action.payload
            }
        case SHOW_MODAL_ADD_TO_CARD:
            return {
                ...state,
                modalProductId: action.payload
            }
        case MODAL_CLOSE:
            return {
                ...state,
                modalProductId: null
            }

        case FAVORITE_PRODUCT:
            return {
                ...state,
                favoriteProduct: action.payload
            }
        default:
            return state
    }
}

export const getProducts = (payload) => ({
    type: GET_PRODUCTS,
    payload
});

export const showModalAddToCard = (payload) => ({
    type: SHOW_MODAL_ADD_TO_CARD,
    payload
});

export const modalClose = () => ({
    type: MODAL_CLOSE
});

export const favoriteProduct = (payload) => ({
    type: FAVORITE_PRODUCT,
    payload
});

// MIDDLEWARE

export const getProductList = () => async (dispatch) => {
    const res = await fetch('cardProduct.json');
    const data = await res.json();
    console.log(data);
    dispatch(getProducts(data));
}

export const modalConfirmAddToCart = (productId) => (dispatch) => {
    addToCart(productId)
    dispatch(modalClose())
}




const store = createStore(reducer, applyMiddleware(thunk));

export default store;
