import React, { useState } from "react";
import PropTypes from 'prop-types';
import { FavoriteList } from "../FavoriteList.jsx/FavoriteList";
import { getProductFavorite } from '../utils/favorite';
// import { setModalProductId } from "./MainPage";


export const FavoritePage = ({ products, setModalProductId, modalProductId, handleModalClose, handleModalConfirm }) => {

    const [list, setList] = useState(getProductFavorite())

    products = products.filter(product => list.find(item => item === product.id))

    const handleFavorite = () => {
        setList(getProductFavorite())
    }

    return (
        <div>
            <div className="container">
                <h1 className="heading-page">Favorite</h1>
                <FavoriteList
                    products={products}
                    setModalProductId={setModalProductId}
                    modalProductId={modalProductId}
                    handleModalClose={handleModalClose}
                    handleModalConfirm={handleModalConfirm}
                    handleFavorite={handleFavorite}
                />
            </div>
        </div>
    )
}


FavoritePage.propTypes = {
    product: PropTypes.object,
    // onAddToCart: PropTypes.func,
}
