/**
 * @param {string} itemId
 */

export function addToCart(itemId) {

    const cart = JSON.parse(localStorage.getItem('cardList')) || {};
    cart[itemId] = (cart[itemId] || 0) + 1;
    localStorage.setItem('cardList', JSON.stringify(cart));
}

export const getProductWithCart = () => {

    const cart = JSON.parse(localStorage.getItem('cardList')) || {};


    const productId = Object.keys(cart);
    const quantityProduct = Object.values(cart)
    console.log(productId);
    console.log(quantityProduct);

    return productId;
}

export function removeProductCart(itemId) {

    const cart = JSON.parse(localStorage.getItem('cardList')) || {};
    console.log(cart[itemId]);
    localStorage.removeItem('cardList', JSON.stringify(cart[itemId]))
}
