import React from 'react';
import { Product } from '../Product/Product';
import { getProductFavorite } from '../utils/favorite';
// import './ProductList/ProductList.scss';



export const FavoriteList = ({ products, setModalProductId }) => {

    const favoriteListItems = getProductFavorite();

    const favoriteList = products.filter(product => favoriteListItems[product.id])
    // const favoriteList = products.forEach(product => {

    //     const favoriteListId = getProductFavorite();

    //     favoriteListId.map(itemId =>

    //         <Product
    //             key={() => (itemId === product.id) ? product.id : product.id}
    //             product={product}

    //         />
    //     )
    // }

    // )

    return (
        <div className="ProductList ">

            {favoriteListItems.length > 0 &&
                favoriteList.map(product =>
                    <Product
                        key={product.id}
                        product={product}
                        onAddToCart={() => setModalProductId(product.id)}
                    />
                )
            }

            {favoriteListItems.length === 0 &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Favorite products not selected
                    </p>
                </div>
            }
        </div>
    )
}
