import React from "react";
import PropTypes from 'prop-types';
import '../Product/Product.scss'
import { Button } from "../Button/Button";


export const CartItem = ({ product, ModalDeleteID, count }) => {


    return (
        <div>
            <hr />
            <div className="width-1000 row justify-cont-between">
                <img
                    className=" width-100 Product__Img"
                    src={product.url}
                    alt={product.name}
                />
                <div className="align-items-center">
                    <p
                        className="Product__Name">
                        {product.name}
                    </p>
                    <p
                        className="Product__id">
                        Код: {product.id}
                    </p>

                    <p>
                        <span className="Product__Color">
                            Цвет:
                        </span>
                        {product.color}
                    </p>
                </div>
                <div className="justify-content-center">
                    <span className="Product__Price">
                        {product.price}
                        <span className="Product__Сurrency">
                            грн
                        </span>
                    </span>
                </div>
                <div className="justify-content-center">
                    Количество: {count}
                </div>
            </div>
            <div>
                <Button
                    className="btn"
                    text="Remove"
                    style={{ backgroundColor: "#0061c2" }}
                    onClick={ModalDeleteID}
                >&times;
            </Button>
            </div>
            <hr />
        </ div >
    )
}

CartItem.propTypes = {
    product: PropTypes.object,
    handelModalConfirm: PropTypes.func,
    count: PropTypes.string
}
