import { ModalDeleteCart } from "../components/ModalDeleteCart/ModalDeleteCart";
import { removeProductCart } from "../utils/cart";
import { connect } from 'react-redux';

const MapStateToProps = (state) => ({
    products: state.products,
    modalDeleteId: state.modalDeleteId
})



export const CartPage = connect(MapStateToProps, {})(({ products, modalDeleteId }) => {

    // const [modalDeleteID, setModalDeleteID] = useState(null);

    // const handleModalClose = () => setModalDeleteID(null);

    // const handleModalConfirm = () => {
    //     removeProductCart(modalDeleteID)
    //     handleModalClose()
    // }

    return (
        <div className="container">
            <h1 className="heading-page">Cart</h1>

            <CartList
                products={products}
            // setModalDeleteID={setModalDeleteID}
            />

            {modalDeleteId &&
                <ModalDeleteCart
                // onClose={handleModalClose}
                // onConfirm={handleModalConfirm}
                />
            }
        </div>
    )
})

CartPage.propTypes = {
    product: PropTypes.object,
}
