/**
 * @param {string} item
 * @param {number} count
 */

function addToCart(item) {
    const cart = JSON.parse(localStorage.getItem('cardList')) || {};
    console.log(cart);
    cart[item] = cart[item] + 1;
    localStorage.setItem('cardList', JSON.stringify(cart));
}



export default addToCart;
