import React from 'react';
import { CartItem } from '../CartItem/CartItem';
import { getProductWithCart, removeProductCart } from '../utils/cart';
// import './ProductList/ProductList.scss';



export const CartList = ({ products, handelModalConfirm }) => {

    const cartListId = getProductWithCart();

    console.log(cartListId['22222']);

    const cartList = products.filter(product => {

        if (cartListId[product.id]) return product;
    })

    console.log(cartList);
    return (
        <div className="ProductList ">
            {Object.keys(cartListId).length > 0 &&
                cartList.map(product => {
                    console.log(product);
                    return (
                        <CartItem
                            product={product}
                            count={cartListId[product.id]}
                        />
                    )
                })
            }


            {
                Object.keys(cartListId).length === 0 &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Your Shopping Cart is Empty
                    </p>
                </div>
            }
        </div>
    )
}

