    // const getProductList = () => {

    //     const productsList = {};

    //     products.forEach(item => {
    //         productsList[item.id] = item;
    //     });

    //     console.log(productList);
    //     return productsList;
    // }

    // getProductList()

    const handleModalClose = () => setModalProductId(null);

    const cartListId = getProductWithCart();

    const cartList = products.forEach((product) => {
        cartListId.map(item => {
            return (
                <Product
                    key={() => (product.id === item) ? product.id : product.id}
                    product={product}

                />
                && <span
                    className="close"
                    onClick={handelModalConfirm}
                >&times;
                    </span>
            )
        }
        )
    })


    return (
        <div className="ProductList ">
            {/* {getProductWithCart && */}
            {/* // cartList
            // }  */}

            {getProductWithCart &&
                <div
                    className="row align-items-center justify-content-center"
                >
                    <p
                        className="notification">
                        Your Shopping Cart is Empty
                    </p>
                </div>
            }
        </div>
    )
}
