import React, { Component } from 'react';
import Button from '../Button/Button';
import './Modal.scss';



class ModalBase extends Component {
  render() {
    const {
      header,
      text,
      textConfirm,
      bgColorHeader,
      bgColorBody,
      bgColorFooter,
      closeButton,
      bgInfo,
      onClose,
      onConfirm
    } = this.props.fields;

    return (
      <div>
        {
          this.state.isOpen &&
          <div
            className="wrapper"
            onClick={onClose}
          >
            <div
              className="modal"
              onClick={(e) => e.stopPropagation()}
            >
              <div
                className="modal-header"
                style={{ backgroundColor: bgColorHeader }}
              >
                <h5 className="modal-title">
                  {header}
                </h5>
                {closeButton &&
                  <span
                    className="close"
                    onClick={onClose}
                  >&times;</span>
                }
              </div>
              <div
                className="modal-body"
                style={{ backgroundColor: bgColorBody }}
              >
                <p className="text-modal">
                  {text}
                </p>
                <p>
                  {textConfirm}
                </p>
              </div>
              <div
                className="modal-footer"
                style={{ backgroundColor: bgColorFooter }}
              >
                <Button onClick={onConfirm} className="btn btn-m1" text='Ok' style={{ backgroundColor: bgInfo }} />
                <Button onClick={() => this.showModal()} className="btn btn-m1" text='Cancel' style={{ backgroundColor: bgInfo }} />
              </div>
            </div>
          </div>
        }
      </div>
    )
  }
}

export default ModalBase;
