import React, { Component } from "react";
import styled from 'styled-components';

const ContainerMaxWidhtStyled = styled.div`
    display:flex;
    flex-direction:column;

`;

const BgImgStyled = styled.img`
    width: 100%;
    height: calc(37.5vw);
    overflow: hidden;
`;

const Heahing = styled.h2`
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    font-size: 20px;
    font-weight: 600;;
    text-transform: uppercase;
    text-align: center;
    color: #424242;
    margin: 30px 30px 8px 30px;

`;

class ContainerMaxWidht extends Component {
    render() {
        return (
            <ContainerMaxWidhtStyled>
                <BgImgStyled src="/img/bgimg.jpg" alt="Img" />
                <Heahing>Top rated</Heahing>
            </ContainerMaxWidhtStyled>
        )
    }
}

export default ContainerMaxWidht;
