import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route, Link, NavLink } from 'react-router-dom'
import { ProductList } from "../ProductList/ProductList";
// import { Product } from "../Product/Product";
import { FavoritePage } from './components/pages/FavoritePage';
import { CartPage } from './components/pages/CartPage';
import { MainPage } from './components/pages/MainPage';
import Header from "../Header/Header";

export const ShopPage = () => {
    return (
        <div>
            <Router>
                <div>
                    <Header />
                    <div className="Menu">
                        <Link className="MenuItems" to="/">HOME</Link>
                        <Link className="MenuItems" to="/shop">SHOP</Link>
                        <Link className="MenuItems" to="/favorite">FAVORITE</Link>
                        <Link className="MenuItems" to="/cart">CART</Link>
                    </div>
                    <Switch>
                        <Route exact path="/" ><MainPage /></Route>
                        <Route path="/shop">SHOP</Route>
                        <Route path="/favorite"><FavoritePage /></Route>
                        <Route path="/cart"><CartPage /></Route>
                    </Switch>

                    <ProductList />
                </div>
            </Router>
        </div>
    )
}
